INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1587, 'Abiraterone', 'Ritonavir', 'Minor', 'Coadministration with ketoconazole, a potent inhibitor of CYP450 3A4, had no clinically relevant effect on the pharmacokinetics of abiraterone in healthy volunteers. Although abiraterone has been shown in vitro to be a substrate of CYP450 3A4, no particular precaution appears necessary when prescribed with potent inhibitors of CYP450 3A4.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1587/', '[1] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.[2] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1588, 'Abiraterone', 'Romidepsin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of romidepsin, which is a substrate of the efflux transporter. Although there are no formal drug interaction studies, the interaction may increase the risk of hematologic toxicities such as anemia, leukopenia, and thrombocytopenia as well as electrocardiographic changes such as QT interval prolongation and T-wave and ST-segment changes.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors. Pharmacologic response to romidepsin should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the romidepsin dosage adjusted as necessary. Patients should have complete blood cell counts, electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1588/', '[1] "Product Information. Istodax (romidepsin)." Gloucester Pharmaceuticals, Cambridge, MA.', 1767363020);
