INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1606, 'Abiraterone', 'Spironolactone', 'Moderate', 'Concomitant use of abiraterone and spironolactone may increase prostate specific antigen (PSA) levels. The exact mechanism is unknown. It has been speculated that spironolactone may act as a mixed androgen receptor agonist/antagonist, and as such display both pro-androgenic and anti-androgenic effects.', NULL, 'Caution is recommended if abiraterone is to be used in combination with spironolactone in patients with metastatic castration-resistant prostate cancer.', NULL, 'Caution is recommended if abiraterone is to be used in combination with spironolactone in patients with metastatic castration-resistant prostate cancer. If this combination is used, monitoring for worsening clinical symptoms and increased levels biochemical markers of prostate cancer such as PSA and alkaline phosphatase levels should be considered.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1606/', '[1] Ferraldeschi R, Sharifi N, Auchus RJ, Attard G "Molecular pathways: Inhibiting steroid biosynthesis in prostate cancer." Clin Cancer Res 19 (2013): 3353-9[2] Sundar S, Dickinson PD "Spironolactone, a possible selective androgen receptor modulator, should be used with caution in patients with metastatic carcinoma of the prostate." BMJ Case Rep 2012 (2012): 1-4[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.[5] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1607, 'Abiraterone', 'St. John''s Wort', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of abiraterone, which is partially metabolized by the isoenzyme.', NULL, 'Concomitant use of abiraterone acetate with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of abiraterone acetate with potent CYP450 3A4 inducers should generally be avoided. Otherwise, an increase in dosing frequency for abiraterone acetate to twice daily during the coadministration period (e.g., from 1000 mg once daily to 1000 mg twice daily) may be considered, although this dosage adjustment has not been studied clinically. The dosage should be adjusted back to the previous level when the CYP450 3A4 inducer is discontinued.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1607/', '[1] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.[2] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[3] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[4] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.[5] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[8] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1608, 'Abiraterone', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways. Conversely, many of these inhibitors are also substrates of CYP450 1A2 and/or 3A4, and coadministration with stiripentol may alter their plasma concentrations as well. Stiripentol is both an inhibitor and inducer of CYP450 1A2 and 3A4 in vitro.', NULL, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways.', NULL, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways. Patients should be monitored for potentially increased adverse effects of stiripentol such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behavior, neutropenia, and thrombocytopenia. In addition, clinical and laboratory monitoring may be appropriate for concomitant drugs whenever stiripentol is added to or withdrawn from therapy. Dosage adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1608/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[6] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1609, 'Abiraterone', 'Sulfamethoxazole', 'Minor', 'Limited data suggest that sulfamethoxazole-trimethoprim (SMX-TMP) may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with SMX-TMP intravenously. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1609/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Crouch MA, Limon L, Cassano AT "Clinical relevance and management of drug-related QT interval prolongation." Pharmacotherapy 23 (2003): 881-908[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wiener I, Rubin D, Martinez E, et al "QT prolongation and paroxysmal ventricular tachycardia occurring during fever following trimethoprim-sulfamethoxazole administration." Mt Sinai J Med 48 (1981): 53-5[5] Lopez JA, Harold JG, Rosenthal MC, Oseran DS, Schapira JN, Peter T "QT prolongation and torsades de pointes after administration of trimethoprin-sulfamethoxazole." Am J Cardiol 59 (1987): 3