INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1884, 'Fosaprepitant', 'Paclitaxel', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of chemotherapeutic agents that are primarily metabolized by CYP450 3A4. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant. In general, the effect of aprepitant on the pharmacokinetics of CYP450 3A4 substrates is expected to be greater when the substrates are administered orally as opposed to intravenously and may be altered following prolonged administration.', NULL, 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials.', NULL, 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials. The potential for increased systemic toxicities of these agents should be considered. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1884/', '[1] Charasson V, Haaz MC, Robert J "Determination of Drug Interactions Occurring with the Metabolic Pathways of Irinotecan." Drug Metab Dispos 30 (2002): 731-733[2] Zhou XJ, Zhou-Pan XR, Gauthier T, Placidi M, Maurel P, Rahmani R "Human liver microsomal cytochrome P450 3A isozymes mediated vindesine biotransformation. Metabolic drug interactions." Biochem Pharmacol 45 (1993): 853-61[3] Clarke SJ, Rivory LP "Clinical pharmacokinetics of docetaxel." Clin Pharmacokinet 36 (1999): 99-114[4] "Product Information. Emend (aprepitant)." Merck & Company Inc, West Point, PA.[5] "Product Information. Emend for Injection (fosaprepitant)." Merck & Company Inc, West Point, PA.[6] Kivisto KT, Kroemer HK, Eichelbaum M "The role of human cytochrome p450 enzymes in the metabolism of anticancer agents: implications for drug interactions." Br J Clin Pharmacol 40 (1995): 523-30', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1885, 'Fosphenytoin', 'Paclitaxel', 'Moderate', 'Theoretically, coadministration with drugs that are inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of paclitaxel, which is metabolized by these isoenzymes.', NULL, 'Clinicians should recognize the potential for interaction with drugs that induce CYP450 2C8 and/or 3A4 and monitor for evidence of reduced therapeutic response to paclitaxel during coadministration.', NULL, 'Clinicians should recognize the potential for interaction with drugs that induce CYP450 2C8 and/or 3A4 and monitor for evidence of reduced therapeutic response to paclitaxel during coadministration.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1885/', '[1] Jamis-Dow CA, Klecker RW, Katki AG, Collins JM "Metabolism of Taxol by human liver microsomes and effect of inhibitors (Meeting abstract)." Proc Annu Meet Am Assoc Cancer Res 34 (1993): a21981993[2] "Product Information. Abraxane (paclitaxel protein-bound)." American Pharmaceutical Partners, Schaumberg, IL.[3] "Product Information. Taxol (paclitaxel)." Bristol-Myers Squibb, Princeton, NJ.[4] Spencer CM, Faulds D "Paclitaxel. A review of its pharmacodynamic and pharmacokinetic properties and therapeutic potential in the treatment of cancer." Drugs 48 (1994): 794-847[5] "Product Information. Taxotere (docetaxel)." Rhone-Poulenc Rorer, Collegeville, PA.[6] Starr SP, Hammann F, Gotta V, et al. "Pharmacokinetic interaction between taxanes and amiodarone leading to severe toxicity." Br J Clin Pharmacol 450 (2016): 22-27[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] Yong WP, Wang LZ, Tham LS, et al. "A phase I study of docetaxel with ketoconazole modulation in patients with advanced cancers." Cancer Chemother Pharmacol 62 (2008): 243-51[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] Aronson JK, Grahame-Smith DG "Clinical pharmacology: adverse drug interactions." Br Med J 282 (1981): 288-91[11] Valenzuela B, Rebollo J, Perez T, Brugarolas A, Perez-Ruixo JJ "Effect of grapefruit juice on the pharmacokinetics of docetaxel in cancer patients: a case report." Br J Clin Pharmacol (2011):[12] McInnes GT, Brodie MJ "Drug interactions that matter: a critical reappraisal." Drugs 36 (1988): 83-110[13] Engels FK, Mathot RA, Loos WJ, van Schaik RH, Verweij J "Influence of high-dose ketoconazole on the pharmacokinetics of docetaxel." Cancer Biol Ther 5 (2006): 833-9', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1886, 'Fostamatinib', 'Paclitaxel', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1886/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1887, 'Gadobenic acid', 'Paclitaxel', 'Moderate', 'Gadobenate dimeglumine may compete with other substrates of the canalicular multispecific organic anion transporter, also known as cMOAT or MRP2, which is an endogenous transporter protein that works in coordination with the conjugation process to facilitate drug excretion. In solid tumor cells, overexpression of MRP2 confers resistance to a wide variety of anticancer chemotherapeutic agents. Substrates of MRP2 include anthracyclines, protease inhibitors, taxanes, vinca alkaloids, cisplatin, etoposide, methotrexate, and tamoxifen. Theoretically, competition for MRP2 transport may interfere with the clearance of these drugs and/or gadobenate dimeglumine.', NULL, 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2.', NULL, 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2. Patients should be monitored for potentially increased pharmacologic effects of gadobenate dimeglumine as well as the coadministered drug(s).', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1887/', '[1] "Product Information. Multihance (gadobenate dimeglumine)." Bracco Diagnostics Inc, Princeton, NJ.[2] Huisman MT, Smit JW, Crommentuyn KM, et al. "Multidrug resistance protein 2 (MRP2) transports HIV protease inhibitors, and transport can be enhanced by other drugs." AIDS 16 (2002): 2295-2301', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1888, 'Ganciclovir', 'Paclitaxel', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1888/', '[1] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[2] Ce