INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2089, 'Isotretinoin', 'Betamethasone', 'Moderate', 'Theoretical concerns exist that isotretinoin and corticosteroids may have additive effects on bone loss. Decreased bone mineral density (osteomalacia, osteopenia, osteoporosis) and bone fractures have been reported with either treatment alone during prolonged administration. Avascular necrosis has also been reported with corticosteroids, particularly when given in high dosages or for prolonged periods. However, no formal clinical trials have been conducted to evaluate whether there is an interactive effect on bone loss during coadministration.', NULL, 'Caution is advised when isotretinoin is used in combination with corticosteroids, particularly in patients with a history of osteomalacia, osteoporosis, or other disorders of bone metabolism.', NULL, 'Caution is advised when isotretinoin is used in combination with corticosteroids, particularly in patients with a history of osteomalacia, osteoporosis, or other disorders of bone metabolism. Bone-related laboratory and radiological tests should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2089/', '[1] Anderton JM, Helm R "Multiple joint osteonecrosis following short-term steroid therapy. Case report." J Bone Joint Surg Am 64 (1982): 139-41[2] Taylor LJ "Multifocal avascular necrosis after short-term high-dose steroid therapy. A report of three cases." J Bone Joint Surg Br 66 (1984): 431-3[3] Ledford D, Apter A, Brenner AM, Rubin K, Prestwood K, Frieri M, Lukert B "Osteoporosis in the corticosteroid-treated patient with asthma." J Allergy Clin Immunol 102 (1998): 353-62[4