INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2094, 'Isotretinoin', 'Cannabidiol', 'Moderate', 'Coadministration of cannabidiol with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Cannabidiol causes dose-related elevations of liver transaminases, both alanine aminotransferase (ALT) and aspartate aminotransferase (AST).', NULL, 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa.', NULL, 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa. Serum transaminases and total bilirubin levels should be obtained prior to initiating cannabidiol, and patients with elevated baseline transaminase levels above 3 times the ULN accompanied by elevations in bilirubin above 2 times the ULN should be evaluated. Patients who develop clinical signs or symptoms suggestive of hepatic dysfunction (e.g., unexplained nausea, vomiting, right upper quadrant abdominal pain, fatigue, anorexia, jaundice, dark urine) should have serum transaminases and total bilirubin measured promptly, and cannabidiol treatment interrupted or discontinued as appropriate. Cannabidiol should be discontinued in patients with elevations of transaminase levels greater than 3 times the ULN and bilirubin levels greater than 2 times the ULN. Patients with sustained transaminase elevations of greater than 5 times the ULN should also have treatment discontinued. Patients with prolonged elevations of serum transaminases should be evaluated for other possible causes. Also consider dosage adjustment or discontinuation of any coadministered medication that is known to affect the liver.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2094/', '[1] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2095, 'Isotretinoin', 'Carbamazepine', 'Moderate', 'A case report has suggested that isotretinoin may decrease the AUC of carbamazepine. Pharmacologic effects may be reduced. The mechanism is unknown.', NULL, 'Clinical monitoring of serum carbamazepine concentrations and patient response is recommended when isotretinoin is added to or discontinued from an existing regimen, or when the dosage is changed.', NULL, 'Clinical monitoring of serum carbamazepine concentrations and patient response is recommended when isotretinoin is added to or discontinued from an existing regimen, or when the dosage is changed. Patients should be advised to notify their physician if they experience a reduction in or loss of efficacy.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2095/', '[1] Marsden JR "Effect of isotretinoin on carbamazepine pharmacokinetics." Br J Dermatol 119 (1988): 403-4[2] Garg SK, Kumar N, Bhargava VK, Prabhakar SK "Effect of grapefruit juice on carbamazepine bioavailability in patients with epilepsy." Clin Pharmacol Ther 64 (1998): 286-8[3] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2096, 'Isotretinoin', 'Cenobamate', 'Moderate', 'Coadministration with cenobamate may decrease the plasma concentrations and pharmacologic effects of CYP450 3A4 substrates. The proposed mechanism is cenobamate-mediated induction of the CYP450 3A4 isoenzyme.', NULL, 'Caution is advised if cenobamate is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic index.', NULL, 'Caution is advised if cenobamate is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic index. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever cenobamate is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2096/', '[1] "Product Information. Xcopri (cenobamate)." SK Life Science, Inc., Paramus, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2097, 'Isotretinoin', 'Clofarabine', 'Moderate', 'The liver is a known target organ for clofarabine toxicity, and concomitant use of other potentially hepatotoxic agents may increase the risk of liver injury. Severe and fatal hepatotoxicity has occurred with the use of clofarabine alone.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Hepatic function should be monitored during clofarabine administration, and therapy discontinued if grade 3 to 4 liver enzyme or bilirubin elevations occur. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2097/', '[1] "Product Information. Clolar (clofarabine)." sanofi-aventis, Bridgewater, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2098, 'Isotretinoin', 'Dabrafenib', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', NULL, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', NULL, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2098/', '[1] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[5] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2099, 'Isotretinoin', 'Deflazacort', 'Moderate', 'Theoretical concerns exist that isotretinoin and corticosteroids may have additive effects on bone loss. Decreased bone mineral density (osteomalacia, osteopenia, osteoporosis) and bone fractures have been reported with either treatment alone during prolonged administration. Avascular necrosis has also been reported with corticosteroids, particularly when given in high dosages or for prolonged periods. However, no formal clinical trials have been conducted to evaluate whether there is an interactive effect on bone loss during coadministration.', NULL, 'Caution is advised when isotretinoin is used in combination with corticosteroids, particularly in patients with a history of osteomalacia, osteoporosis, or other disorders of bone metabolism.', NULL, 'Caution is advised when isotretinoin is used in combination with corticosteroids, particularly in patients with a history of osteomalacia, osteoporosis, or other disorders of bone metabolism. Bone-related laboratory and radiological tests should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2099/', '[1] Anderton JM, Helm R "Multiple joint osteonecrosis following short-term steroid therapy. Case report." J Bone Joint Surg Am 64 (1982): 139-41[2] Taylor LJ "Multifocal avascular necrosis after short-term high-dose steroid therapy. A report of three cases." J Bone Joint Surg Br 66 (1984): 431-3[3] Ledford D, Apter A, Brenner AM, Rubin K, Prestwood K, Frieri M, Lukert B "Osteoporosis in the corticosteroid-treated patient with asthma." J Allergy Clin Immunol 102 (1998): 353-62[4] 