INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2103, 'Isotretinoin', 'Doxycycline', 'Major', 'Concomitant use of vitamin A or related compounds (i.e., retinoids) with tetracyclines may increase the risk of pseudotumor cerebri, also known as benign intracranial hypertension. These agents individually have been associated with the development of pseudotumor cerebri and may have additive effects during coadministration. The interaction has been suspected in reported cases involving the use of isotretinoin or vitamin A in combination with a tetracycline.', NULL, 'Coadministration of most retinoids with tetracyclines is considered contraindicated.', NULL, 'Coadministration of most retinoids with tetracyclines is considered contraindicated. Patients treated with a retinoid or a tetracycline should be advised to discontinue the medication and notify their physician immediately if they develop early symptoms of pseudotumor cerebri such as headache, nausea, vomiting, visual disturbances, vision loss, and papilledema. Although intracranial hypertension typically resolves after discontinuation of treatment, the possibility for permanent visual loss exists. Therefore, prompt ophthalmologic evaluation should be performed if visual disturbances occur. Since intracranial pressure may remain elevated for weeks after drug cessation, patients should be monitored until they stabilize.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2103/', '[1] Gardner K, Cox T, Digre KB "Idiopathic intracranial hypertension associated with tetracycline use in fraternal twins: case reports and review." Neurology 45 (1995): 6-10[2] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[3] Walters BN, Gubbay SS "Tetracycline and benign intracranial hypertension: report of five cases." Br Med J 282 (1981): 19-20[4] Moskowitz Y, Leibowitz E, Ronen M, Aviel E "Pseudotumor cerebri induced by vitamin A combined with minocycline." Ann Ophthalmol 25 (1993): 306-8[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Weese-Mayer DE, Yang RJ, Mayer JR, Zaparackas Z "Minocycline and Pseudotumor cerebri: The well-known but well-kept secret." Pediatrics 108 (2001): 519-20[7] Donnet A, Dufour H, Graziani N, Grisoli F "Minocycline and benign intracranial hypertension." Biomed Pharmacother 46 (1992): 171-2[8] Yokokura M, Hatake K, Komatsu N, Kajitani H, Miura Y "Toxicity of tretinoin in acute promyelocytic leukaemia." Lancet 343 (1994): 361-2[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] Chan AY, Liu DT, Friedman DI, Gordon LK, Egan RA "Doxycycline and intracranial hypertension." Neurology 64 (2005): 765-6[11] Chiu AM, Chuenkongkaew WL, Cornblath WT, Trobe JD, Digre KB, Dotan SA, Musson KH, Eggenberger ER "Minocycline treatment and pseudotumor cerebri syndrome." Am J Ophthalmol 126 (1998): 116-21[12] Lewis PA, Kearney PJ "Pseudotumor cerebri induced by minocycline treatment for acne vulgaris." Acta Derm Venereol 77 (1997): 83[13] Lee AG "Pseudotumor cerebri after treatment with tetracycline and isotretinoin for acne." Cutis 55 (1995): 165-8[14] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[15] "Product Information. Vesanoid (tretinoin)." Roche Laboratories, Nutley, NJ.[16] Schmitt K, Schwarz R, Tulzer G, Krieger O, Zoubek A, Gadner H "Toxicity of tretinoin in acute promyelocytic leukaemia." Lancet 343 (1994): 361[17] Minutello JS, Dimayuga RG, Carter J "Pseudotumor cerebri, a rare adverse reaction to tetracycline therapy." J Periodontol 59 (1988): 848-51[18] "Product Information. Soriatane (acitretin)." Roche Laboratories, Nutley, NJ.[19] "Product Information. Accutane (isotretinoin)." Roche Laboratories, Nutley, NJ.[20] Tabibian JH, Gutierrez MA "Doxycycline-induced pseudotumor cerebri." South Med J 102 (2009): 310-1[21] Cuddihy J "Case report of benign intercranial hypertension secondary to tetracycline." Ir Med J 87 (1994): 90[22] Roytman M, Frumkin A, Bohn TG "Pseudotumor cerebri caused by isotretinoin." Cutis 42 (1988): 399-400[23] Delaney RA, Narayanaswamy TR "Pseudo-tumor cerebri and acne." Mil Med 155 (1990): 511[24] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[25] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2104, 'Isotretinoin', 'Echinacea', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2104/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2105, 'Isotretinoin', 'Efavirenz', 'Moderate', 'Coadministration with efavirenz may decrease the plasma concentrations of retinoids that are substrates of the CYP450 3A4 isoenzyme such as alitretinoin, bexarotene, isotretinoin, and tretinoin. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by efavirenz. Coadministration of efavirenz with other agents known to induce hepatotoxicity, including retinoids, may potentiate the risk of liver injury. Efavirenz has been associated with hepatotoxicity during postmarketing use.', NULL, 'Caution is advised if efavirenz must be used concomitantly with retinoids that undergo metabolism by CYP450 3A4.', NULL, 'Caution is advised if efavirenz must be used concomitantly with retinoids that undergo metabolism by CYP450 3A4. Pharmacologic response to the retinoid should be monitored more closely whenever efavirenz is added to or withdrawn from therapy, and the retinoid dosage adjusted as necessary. The risk of hepatic injury should also be considered during treatment with this combination. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, light-coloured stools, and jaundice. Monitoring of liver function tests should occur before and during treatment, especially in patients with underlying hepatic disease (including hepatitis B or C coinfection) or marked transaminase elevations. The benefit of continued therapy with efavirenz and/or the retinoid should be considered against the unknown risks of significant liver toxicity in patients who develop persistent elevations of serum transaminases greater than five times the upper limit of normal.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2105/', '[1] "Product Information. Accutane (isotretinoin)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Vesanoid (tretinoin)." Roche Laboratories, Nutley, NJ.[3] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[4] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2106, 'Isotretinoin', 'Enzalutamide', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', NULL, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2106/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Xtandi (enzalutamide)." Astellas Pharma US, Inc, Deerfield, IL.[3] Benoist G, van Oort I, et al "Drug-drug interaction potential in men treated with enzalutamide: Mind the gap." Br J Clin Pharmacol 0 (2017): epub[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2107, 'Isotretinoin', 'Epirubicin', 'Moderate', 'The concomitant or sequential use of epirubicin with agents known to induce hepatotoxicity may potentiate the risk of liver injury. Epirubicin undergoes elimination through the hepatobiliary system.', NULL, 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', NULL, 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2107/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Ellence (epirubicin)" Pharmacia and Upjohn, Kalamazoo, MI.[4] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365203);
