INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2295, 'Fentanyl', 'Desflurane', 'Moderate', 'Opioids may be expected to decrease the minimum alveolar concentration required for inhalation anesthetics. When these agents are used concomitantly, less inhalation anesthetic may be required.', NULL, 'In general, anesthesia should be titrated to clinical effect by those experienced in anesthesia technique and in the care of patients receiving opioids.', NULL, 'In general, anesthesia should be titrated to clinical effect by those experienced in anesthesia technique and in the care of patients receiving opioids.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2295/', '[1] "Product Information. Ultane (sevoflurane)." Abbott Pharmaceutical, Abbott Park, IL.[2] Inagaki Y, Kuzukawa A "Effects of epidural and intravenous buprenorphine on halothane minimum alveolar anesthetic concentration and hemodynamic responses." Anesth Analg 84 (1997): 100-5[3] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2296, 'Fentanyl', 'Desipramine', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors, monoamine oxidase inhibitors, tricyclic antidepressants, 5-HT1 receptor agonists, ergot alkaloids, cyclobenzaprine, lithium, St. John''s wort, phenylpiperidine opioids, dextromethorphan, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a pro