INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2297, 'Fentanyl', 'Desmopressin', 'Moderate', 'Coadministration with opiates may increase the plasma concentrations and pharmacologic effects of oral desmopressin. The risk of water intoxication and/or hyponatremia may be increased.', NULL, 'Caution is recommended if desmopressin is used in combination with opiates.', NULL, 'Caution is recommended if desmopressin is used in combination with opiates. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly. Patients should be advised to seek immediate medical attention if they develop early signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, drowsiness, lethargy, weakness, listlessness, headache, confusion, difficulty concentrating, memory impairment, anuria, and weight gain. Early treatment may help prevent progression to seizure, coma, respiratory arrest, and death.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2297/', '[1] Appel WC "Possible roles of normeperidine and hyponatremia in a postoperative death." Can Med Assoc J 137 (1987): 912-3[2] Sarret D, Le Berre JP, Zemraoui N "Tramadol-induced hyponatremia." Am J Kidney Dis 52 (2008): 1026; author reply 1027[3] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company, Norwalk, CT.[4] "Product Information. Stimate (desmopressin)." Forest Pharmaceuticals, St. Louis, MO.[5] Callreus T, Lundahl J, Hoglund P, Bengtsson P "Changes in gastrointestinal motility influence the absorption of desmopressin." Eur J Clin Pharmacol 55 (1999): 305-9[6] Kokko H, Hall PD, Afrin LB "Fentanyl-associated sy