INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324903, 'Abemaciclib', 'Nilotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of abemaciclib and its pharmacologically active metabolites, all of which are substrates of the isoenzyme.', 'DDInter', 1767317474, 'Caution is advised when abemaciclib is used with moderate CYP450 3A4 inhibitors. Dosage adjustments for abemaciclib in 50 mg decrements may be necessary and should be done in accordance with the product labeling. Patients should be monitored for potentially increased adverse effects such as nausea, vomiting, diarrhea, stomatitis, venous thromboembolism, hepatotoxicity, anemia, neutropenia, and thrombocytopenia.', 'Metabolism', 'Caution is advised when abemaciclib is used with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324904, 'Abemaciclib', 'Niraparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with niraparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with niraparib.', 'DDInter', 1767317474, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', 'Synergism', 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324905, 'Abemaciclib', 'Ocrelizumab', 'Moderate', 'The use of the CD20-directed cytolytic antibody ocrelizumab with other immune-modulating or immunosuppressive therapy, including immunosuppressant doses of corticosteroids may increase the risk of infections.', 'DDInter', 1767317474, 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy. In addition, the prolonged immunosuppressant effects and mode of action of other immunosuppressant drugs such as daclizumab, fingolimod, natalizumab, teriflunomide, or mitoxantrone should be considered prior to starting ocrelizumab therapy. Patients should be advised to notify their doctor if they develop signs or symptoms of infection, including upper or lower respiratory tract infection, skin infection, herpes related infection, or PML.', 'Synergism', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324906, 'Abemaciclib', 'Olaparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with olaparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with olaparib.', 'DDInter', 1767317474, 'Caution is advised if olaparib is prescribed with other myelosuppressive agents. Complete blood counts should be performed at baseline and monthly thereafter. Do not start olaparib until patients have recovered from hematologic toxicity caused by previous chemotherapy.', 'Synergism', 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324907, 'Abemaciclib', 'Omacetaxine mepesuccinate', 'Moderate', 'The use of omacetaxine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Omacetaxine alone may cause severe myelosuppression, neutropenia, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other hematotoxic therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317474, 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms such as fever, chills, shortness of breath, fatigue, and any unusual bleeding or bruising.', 'Synergism', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, NULL, 'Moderate', 'pharmacodynamic');
