INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324922, 'Abemaciclib', 'Poliovirus type 1 antigen (formaldehyde inactivated)', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317474, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324923, 'Abemaciclib', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of abemaciclib and its pharmacologically active metabolites, all of which are substrates of the isoenzyme.', 'DDInter', 1767317474, 'When used with potent CYP450 3A4 inhibitors other than ketoconazole (which is not recommended), patients who would otherwise be on abemaciclib 200 mg or 150 mg twice daily should have their dosage reduced to 100 mg twice daily. In patients who have already had a dosage reduction to 100 mg twice daily due to adverse reactions, a further reduction of the abemaciclib dosage to 50 mg twice daily is recommended. Following discontinuation of the potent CYP450 3A4 inhibitor, abemaciclib dosage should be increased (after 3 to 5 half-lives of the inhibitor) to the dosage that was used before starting the inhibitor.', 'Metabolism', 'When used with potent CYP450 3A4 inhibitors other than ketoconazole (which is not recommended), patients who would otherwise be on abemaciclib 200 mg or 150 mg twice daily should have their dosage reduced to 100 mg twice daily.', NULL, NULL, 'Major', 'pharmacodynamic');
