INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324940, 'Abemaciclib', 'Sargramostim', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', 'DDInter', 1767317474, 'When hematopoietic growth factors are required, abemaciclib should be suspended for at least 48 hours after the last dose of the growth factor and until hematologic toxicity resolves to grade 2 or below.', 'Others', 'When hematopoietic growth factors are required, abemaciclib should be suspended for at least 48 hours after the last dose of the growth factor and until hematologic toxicity resolves to grade 2 or below.', NULL, NULL, 'Moderate', 'pharmacodynamic');
