INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324952, 'Abemaciclib', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of abemaciclib and its pharmacologically active metabolites, all of which are substrates of the isoenzyme.', 'DDInter', 1767317474, 'Caution is advised when abemaciclib is used with moderate CYP450 3A4 inhibitors. Dosage adjustments for abemaciclib in 50 mg decrements may be necessary and should be done in accordance with the product labeling. Patients should be monitored for potentially increased adverse effects such as nausea, vomiting, diarrhea, stomatitis, venous thromboembolism, hepatotoxicity, anemia, neutropenia, and thrombocytopenia.', 'Metabolism', 'Caution is advised when abemaciclib is used with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
