INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324955, 'Abemaciclib', 'Talimogene laherparepvec', 'Major', 'Talimogene laherparepvec is a live, attenuated herpes simplex virus. Administration during immunosuppressant or intense antineoplastic therapy may be associated with a risk of potentially life-threatening disseminated herpetic infection due to enhanced virus replication in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317474, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Treatment with talimogene laherparepvec may need to be deferred until after such therapy is discontinued and the patient''s immune system has sufficiently recovered.', 'Synergism', 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324956, 'Abemaciclib', 'Telaprevir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of abemaciclib and its pharmacologically active metabolites, all of which are substrates of the isoenzyme.', 'DDInter', 1767317474, 'When used with potent CYP450 3A4 inhibitors other than ketoconazole (which is not recommended), patients who would otherwise be on abemaciclib 200 mg or 150 mg twice daily should have their dosage reduced to 100 mg twice daily. In patients who have already had a dosage reduction to 100 mg twice daily due to adverse reactions, a further reduction of the abemaciclib dosage to 50 mg twice daily is recommended. Following discontinuation of the potent CYP450 3A4 inhibitor, abemaciclib dosage should be increased (after 3 to 5 half-lives of the inhibitor) to the dosage that was used before starting the inhibitor.', 'Metabolism', 'When used with potent CYP450 3A4 inhibitors other than ketoconazole (which is not recommended), patients who would otherwise be on abemaciclib 200 mg or 150 mg twice daily should have their dosage reduced to 100 mg twice daily.', NULL, NULL, 'Major', 'pharmacodynamic');
