INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325006, 'Abiraterone', 'Atomoxetine', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 2D6 may increase the plasma concentrations of atomoxetine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317474, 'Pharmacologic response to atomoxetine should be monitored more closely whenever a CYP450 2D6 inhibitor is added to or withdrawn from therapy, as dosage adjustment of atomoxetine may be necessary in extensive metabolizers. During coadministration, patients should be advised to contact their physician if they experience excessive adverse effects of atomoxetine such as dizziness, dry mouth, anorexia, sleep disturbances, and palpitations.', 'Metabolism', 'Pharmacologic response to atomoxetine should be monitored more closely whenever a CYP450 2D6 inhibitor is added to or withdrawn from therapy, as dosage adjustment of atomoxetine may be necessary in extensive metabolizers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325007, 'Abiraterone', 'Avapritinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9.', 'DDInter', 1767317474, 'Caution is advised when avapritinib is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development of serious adverse effects such as intracranial hemorrhage, cognitive impairment, mood disorders, hallucinations, edema, and decreases in hemoglobin, leukocytes, neutrophils and platelets, and the dosing of avapritinib adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when avapritinib is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
