INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325015, 'Abiraterone', 'Brentuximab vedotin', 'Moderate', 'Coadministration with potent CYP450 3A4 inhibitors or P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin. MMAE is primarily metabolized by CYP450 3A4 and has been found in vitro to be a substrate of the P-gp efflux transporter. Coadministration of brentuximab vedotin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Cases were consistent with hepatocellular injury, including elevations of transaminases and/or bilirubin, and typically occurred after the first dose or after a rechallenge. Preexisting liver disease and elevated baseline liver enzymes may also increase the risk.', 'DDInter', 1767317474, 'Caution is advised when brentuximab is used with potent CYP450 3A4 inhibitors (e.g., azole antifungal agents, clarithromycin, erythromycin, nefazodone, ritonavir, telithromycin) or P-gp inhibitors (e.g., protein kinase inhibitors, abiraterone, amiodarone, azithromycin, cyclosporine, dronedarone, ivacaftor) that are also potentially hepatotoxic. Close monitoring for adverse effects including neutropenia, infection, peripheral neuropathy, and hepatotoxicity is recommended. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.', 'Metabolism, Synergism', 'Caution is advised when brentuximab is used with potent CYP450 3A4 inhibitors (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325016, 'Abiraterone', 'Brexpiprazole', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may significantly increase the plasma concentrations of brexpiprazole, which is metabolized by these isoenzymes.', 'DDInter', 1767317474, 'The manufacturer recommends that the brexpiprazole dosage be reduced as follows during concomitant administration of CYP450 inhibitors. The brexpiprazole dosage should be increased to the original level if these agents are discontinued. It is advisable to monitor patients for clinical response.', 'Metabolism', 'The manufacturer recommends that the brexpiprazole dosage be reduced as follows during concomitant administration of CYP450 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325017, 'Abiraterone', 'Brigatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317474, 'If coadministration with CYP450 3A4 inhibitors is needed, patients should be closely monitored for increased adverse reactions such as nausea, vomiting, diarrhea, hypertension, bradycardia, hyperglycemia, visual disturbances, lymphopenia, anemia, and elevations in pancreatic enzymes and creatine phosphokinase.', 'Metabolism', 'If coadministration with CYP450 3A4 inhibitors is needed, patients should be closely monitored for increased adverse reactions such as nausea, vomiting, diarrhea, hypertension, bradycardia, hyperglycemia, visual disturbances, lymphopenia, anemia, and elevations in pancreatic enzymes and creatine phosphokinase.', NULL, NULL, 'Moderate', 'pharmacodynamic');
