INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325049, 'Abiraterone', 'Dabrafenib', 'Moderate', 'Coadministration with abiraterone acetate may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2C8. Abiraterone is a potent inhibitor of CYP450 2C8 in vitro.', 'DDInter', 1767317474, 'Caution is advised if abiraterone acetate is prescribed with medications that undergo metabolism by CYP450 2C8, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever abiraterone acetate is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if abiraterone acetate is prescribed with medications that undergo metabolism by CYP450 2C8, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
