INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325060, 'Abiraterone', 'Dextromethorphan', 'Moderate', 'Coadministration with CYP450 2D6 inhibitors may increase the plasma concentrations of dextromethorphan in patients who are extensive metabolizers of this isoenzyme (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent). The proposed mechanism is inhibition of the CYP450 2D6-mediated O-demethylation of dextromethorphan. Increased plasma concentrations increase the risk of dextromethorphan-related adverse effects (e.g., agitation, confusion, tremor, insomnia, diarrhea, and respiratory depression) and serotonin syndrome.', 'DDInter', 1767317474, 'Caution should be exercised if these drugs must be used together. Patients should be monitored for signs of dextromethorphan adverse effects (e.g., agitation, confusion, tremor, insomnia, diarrhea, and respiratory depression) and serotonin syndrome, and advised to notify their health care professional if these adverse effects develop or worsen. Dose reduction of dextromethorphan may also be required.', 'Metabolism', 'Caution should be exercised if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325061, 'Abiraterone', 'Diclofenac', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of diclofenac, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317474, 'Caution is advised if diclofenac is prescribed in combination with an inhibitor of CYP450 2C9.', 'Metabolism', 'Caution is advised if diclofenac is prescribed in combination with an inhibitor of CYP450 2C9.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325062, 'Abiraterone', 'Dihydroergotamine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317474, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors. Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
