INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325096, 'Abiraterone', 'Fenfluramine', 'Moderate', 'Coadministration with abiraterone acetate may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by abiraterone.', 'DDInter', 1767317474, 'Caution is advised if abiraterone acetate must be used concurrently with drugs that undergo metabolism by CYP450 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever abiraterone acetate is added to or withdrawn from therapy. Concomitant use of abiraterone acetate and substrates of CYP450 2D6 with a narrow therapeutic index (e.g., antiarrhythmics) should be avoided if possible.', 'Metabolism', 'Caution is advised if abiraterone acetate must be used concurrently with drugs that undergo metabolism by CYP450 2D6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325097, 'Abiraterone', 'Fingolimod', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', 'DDInter', 1767317474, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
