INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325141, 'Istradefylline', 'Abiraterone', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1.', 'DDInter', 1767317474, 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inhibitors. It is unknown if istradefylline will require a dosage adjustment if coadministered with a moderate CYP450 3A4 inhibitor. Clinical and laboratory monitoring of istradefylline should be considered whenever a moderate CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be monitored for adverse effects including dyskinesia, impulse control issues, dizziness, constipation, nausea, hallucinations, and insomnia.', 'Metabolism', 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
