INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5014, 'Alprazolam', 'Codeine', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'Naloxone, Naltrexone, Benzonatate', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5018/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5015, 'Alvimopan', 'Codeine', 'Major', 'Recent exposure to opioids can increase the sensitivity to mu opioid receptor antagonists.', NULL, 'The use of alvimopan is contraindicated in patients who have received therapeutic doses of opioids for more than 7 consecutive days immediately prior to alvimopan administration.', NULL, 'The use of alvimopan is contraindicated in patients who have received therapeutic doses of opioids for more than 7 consecutive days immediately prior to alvimopan administration. Caution is advised in patients who have received more than 3 doses of an opioid within the week prior to surgery, as these patients were not studied in the postoperative ileus clinical trials. Patients with recent exposure to opioids should be monitored for increased adverse effects such as abdominal pain, nausea and vomiting, and diarrhea.', 'Synergism', 'Naltrexone, Tapentadol, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone, Benzonatate, Clofedanol, Dextromethorphan, Pentoxyverine', 'Sorbitol, Tegaserod, Naloxegol, Bisacodyl, Naldemedine, Lactulose, Methylnaltrexone, Lactitol, Magnesium oxide, Prucalopride, Linaclotide, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5019/', '[1] "Product Information. Entereg (alvimopan)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.[3] "Product Information. Entereg (alvimopan)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
