INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5168, 'Galantamine', 'Codeine', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 2D6 may increase the plasma concentrations of galantamine, which is primarily metabolized by the isoenzyme. In multiple-dose pharmacokinetic studies, a potent inhibitor such as paroxetine has been shown to increase the area under the plasma concentration-time curve (AUC) of galantamine by 40%.', NULL, 'During concomitant therapy with drugs that inhibit CYP450 2D6 activity, the possibility of prolonged and/or increased pharmacologic effects of galantamine should be considered.', NULL, 'During concomitant therapy with drugs that inhibit CYP450 2D6 activity, the possibility of prolonged and/or increased pharmacologic effects of galantamine should be considered. Patients should be advised to notify their physician if they experience nausea, vomiting, sweating, weakness, salivation, or slower respirations.', 'Metabolism', 'Morphine, Naltrexone, Oxymorphone, Tapentadol, Oxycodone, Butorphanol, Meperidine, Hydromorphone, Dezocine, Opium, Ibuprofen, More', 'Rivastigmine, Donepezil, Aducanumab, Memantine, Tacrine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5172/', '[1] "Product Information. Reminyl (galantamine)" Janssen Pharmaceuticals, Titusville, NJ.', 1767369485);
