INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5442, 'Dichloralphenazone', 'Apomorphine', 'Moderate', 'Central nervous system (CNS) depressant effects may be additively or synergistically increased in patients using apomorphine in combination with other drugs that can also cause these effects. Apomorphine alone has been frequently associated with somnolence and dizziness. Patients may suddenly fall asleep during activities of daily living.', NULL, 'The use of other sedating drugs should generally be avoided during apomorphine treatment.', NULL, 'The use of other sedating drugs should generally be avoided during apomorphine treatment. Patients prescribed these agents concurrently should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Darifenacin, Vardenafil, Tadalafil, Ammonium chloride, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, More', 'Melatonin, Nitrazepam, Zopiclone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5446/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.[2] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.', 1767369485);
