INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5554, 'Isometheptene', 'Levomilnacipran', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Dicyclomine, Mepenzolate, Alosetron, Glycopyrronium, Papaverine, Propantheline', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Vortioxetine, Doxepin, Viloxazine, Amoxapine, Imipramine, Esketamine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5558/', '[1] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[4] "Prod