INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5642, 'Isometheptene', 'Sibutramine', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when sibutramine is combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake.', NULL, 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating therapy with sibutramine and monitored at regular intervals thereafter. Dose reduction or discontinuation of sibutramine should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Dexfenfluramine, Naltrexone, Ephedrine, Fenfluramine, Orlistat, Lorcaserin', 'Dicyclomine, Mepenzolate, Alosetron, Glycopyrronium, Papaverine, Propantheline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5646/', '[1] "Product Information. Meridia (sibutramine)." Knoll Pharmaceutical Company, Whippany, NJ.[2] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[3] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[4] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5643, 'Dichloralphenazone', 'Silodosin', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Nitrazepam, Thiopental, Eszopiclone, Ramelteon, Scopolamine, Methohexital, Estazolam, Zopiclone, Tasimelteon, Melatonin, Valerian', 'Terazosin, Tadalafil, Finasteride, Dutasteride, Solifenacin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5647/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemet