INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5735, 'Buprenorphine', 'Tramadol', 'Major', 'Concomitant use of tramadol increases the seizure risk in patients taking other opioids. These agents are often individually epileptogenic and may have additive effects on seizure threshold during coadministration. CNS- and respiratory-depressant effects may also be additive. In patients who have been previously dependent on or chronically using opioids, tramadol can also reinitiate physical dependence or precipitate withdrawal symptoms. Mixed opioid agonist-antagonist analgesics such as buprenorphine, butorphanol, nalbuphine, and pentazocine may theoretically decrease the analgesic effects of tramadol or cause withdrawal symptoms in patients who have been taking tramadol.', NULL, 'Concomitant use of tramadol and other opioids, including mixed agonist/antagonist analgesics, should be avoided in general.', NULL, 'Concomitant use of tramadol and other opioids, including mixed agonist/antagonist analgesics, should be avoided in general. Tramadol should not be used in opioid-dependent patients, and use in patients who are chronically on opioids is also not recommended. Tramadol is contraindicated in patients with acute opioid intoxication. Tramadol dosage should be reduced if it must be used in patients receiving opioids. Patients should be monitored for development of seizures and CNS and respiratory depression.', 'Synergism', 'Buprenorphine', 'Nalmefene, Disulfiram, Acamprosate, Tramadol', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5739/', '[1] "Product Information. Ultram (tramadol)." McNeil Pharmaceutical, Raritan, NJ.[2] Miller LG "Recent developments in the study of the effects of cigarette smoking on clinical pharmacokinetics and clinical pharmacodynamics." Clin Pharmacokinet 17 (1989): 90-108[3] D''Arcy PF "Tobacco smoking and drugs: a clinically important interaction?" Drug Intell Clin Pharm 18 (1984): 302-7[4] "Product Information. Talacen (acetaminophen-pentazocine)." Sanofi-Synthelabo Inc, New York, NY.[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[8] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5736, 'Bupropion', 'Tramadol', 'Major', 'The risk of seizures may be increased during coadministration of tramadol with any substance that can reduce the seizure threshold, such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. These agents are often individually epileptogenic and may have additive effects when combined. Many of these agents also exhibit CNS- and/or respiratory-depressant effects, which may be enhanced during their concomitant use with tramadol.', NULL, 'Caution is advised if tramadol is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.', NULL, 'Caution is advised if tramadol is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.g., head trauma, brain tumor, metabolic disorders, alcohol and drug withdrawal, CNS infections).', 'Synergism', 'Naloxone', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5740/', '[1] "Product Information. Ultram (tramadol)." McNeil Pharmaceutical, Raritan, NJ.[2] Gardiner JS, Blough D, Drinkard CR, et al. "Tramadol and seizures: a surveillance study in a managed care population." Pharmacotherapy 20 (2000): 1423-31[3] Pisani F, Spina E, Oteri G "Antidepressant drugs and seizure susceptibility: from in vitro data to clinical practice." Epilepsia 40(Suppl 10) (1999): S48-56[4] Rosenstein DL, Nelson JC, Jacobs SC "Seizures associated with antidepressants: a review." J Clin Psychiatry 54 (1993): 289-99[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5737, 'Buspirone', 'Tramadol', 'Major', 'Concomitant use of opioids with other central nervous system (CNS) depressants (e.g., benzodiazepines, sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. Concomitant use of phenylpiperidine opioids, tapentadol, or tramadol with each other or with other serotonergic agents may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', NULL, 'The use of opioids in conjunction with other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary. Moderately ill patients may also benefit from the administration of a serotonin antagonist (e.g., cyproheptadine, chlorpromazine). Severe cases should be managed under consultation with a toxicologist and may require sedation, neuromuscular paralysis, intubation, and mechanical ventilation in addition to the other measures.', 'Synergism', 'Naloxone', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5741/', '[1] Weiner AL "Meperidine as a potential cause of serotonin syndrome in the emergency department." Acad Emerg Med 6 (1999): 156-8[2] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[3] Keegan MT, Brown DR, Rabinstein AA "Serotonin syndrome from the interaction of cyclobenzaprine with other serotoninergic drugs." Anesth Analg 103 (2006): 1466-8[4] Insel TR, Roy BF, Cohen RM, Murphy DL "Possible development of the serotonin syndrome in man." Am J Psychiatry 139 (1982): 954-5[5] Giese SY, Neborsky R "Serotonin syndrome: potential consequences of Meridia combined with Demerol or fentanyl." Plast Reconstr Surg 107 (2001): 293-4[6] Mills KC "Serotonin syndrome: A clinical update." Crit Care Clin 13 (1997): 763[7] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):[8] "Product Information. Ultram (tramadol)." McNeil Pharmaceutical, Raritan, NJ.[9] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[10] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[11] Nierenberg DW, Semprebon M "The central nervous system serotonin syndrome." Clin Pharmacol Ther 53 (1993): 84-8[12] Chan BSH, Graudins A, Whyte IM, Dawson AH, Braitberg G, Duggin GG "Serotonin syndrome resulting from drug interactions." Med J Aust 169 (1998): 523-5[13] Boyer EW, Shannon M "The serotonin syndrome." N Engl J Med 352 (2005): 1112-20[14] Sternbach H "The serotonin syndrome." Am J Psychiatry 148 (1991): 705-13[15] Nijhawan PK, Katz G, Winter S "Psychiatric illness and the serotonin syndrome: an emerging adverse drug effect leading to intensive care unit admission." Crit Care Med 24 (1996): 1086-9[16] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[17] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[18] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[19] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5738, 'Butabarbital', 'Tramadol', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'Naltrexone, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Nitrazepam', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5742/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767369485);
