INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5923, 'Tramadol', 'Lasmiditan', 'Moderate', 'Coadministration with alcohol or other central nervous system (CNS) depressants may enhance the sedative effects of lasmiditan and increase the likelihood and/or severity of cognitive and/or neuropsychiatric adverse reactions. Patients treated with lasmiditan are at risk of CNS depression, including dizziness and sedation, as well as driving impairment.', NULL, 'Caution is advised during coadministration of lasmiditan with alcohol, other CNS depressants, or other agents that cause sedation.', NULL, 'Caution is advised during coadministration of lasmiditan with alcohol, other CNS depressants, or other agents that cause sedation. Patients should be advised against driving and other activities that require complete mental alertness for at least 8 hours after lasmiditan is administered.', 'Synergism', 'Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Rimegepant, Eptinezumab, Ubrogepant, Galcanezumab, Atogepant, Methysergide, Erenumab, Fremanezumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5927/', '[1] "Product Information. Reyvow (lasmiditan)." Lilly, Eli and Company, Indianapolis, IN.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka