INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325251, 'Abiraterone', 'Primidone', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of abiraterone, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317474, 'Concomitant use of abiraterone acetate with potent CYP450 3A4 inducers should generally be avoided. Otherwise, an increase in dosing frequency for abiraterone acetate to twice daily during the coadministration period (e.g., from 1000 mg once daily to 1000 mg twice daily) may be considered, although this dosage adjustment has not been studied clinically. The dosage should be adjusted back to the previous level when the CYP450 3A4 inducer is discontinued.', 'Metabolism', 'Concomitant use of abiraterone acetate with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
