INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325253, 'Abiraterone', 'Procainamide', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317474, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325254, 'Abiraterone', 'Prochlorperazine', 'Moderate', 'Coadministration with abiraterone acetate may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by abiraterone.', 'DDInter', 1767317474, 'Caution is advised if abiraterone acetate must be used concurrently with drugs that undergo metabolism by CYP450 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever abiraterone acetate is added to or withdrawn from therapy. Concomitant use of abiraterone acetate and substrates of CYP450 2D6 with a narrow therapeutic index (e.g., antiarrhythmics) should be avoided if possible.', 'Metabolism', 'Caution is advised if abiraterone acetate must be used concurrently with drugs that undergo metabolism by CYP450 2D6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
