INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325266, 'Abiraterone', 'Radium Ra 223 dichloride', 'Major', 'The concomitant use of radium Ra 223 dichloride with abiraterone and prednisone/prednisolone may increase the risk of death and fractures in patients with metastatic castration-resistant prostate cancer (CRPC).', 'DDInter', 1767317474, 'Until more information is available, use of radium Ra 223 dichloride with abiraterone and prednisone/prednisolone should be considered contraindicated. US authorities are prohibiting the start of new treatments with this combination and suspending use in patients currently receiving these drugs in combination in favor of other therapeutic alternatives. Some authorities recommend that subsequent treatment with radium Ra 223 dichloride should not be initiated for at least 5 days after the last administration of abiraterone in combination with prednisone/prednisolone (AU, UK, EU). In addition, continued monitoring for fractures should be considered for patients previously treated with the combination. Preliminary evidence suggests that use of bone health agents such as bisphosphonates or denosumab may diminish the risk of fractures.', 'Synergism', 'Until more information is available, use of radium Ra 223 dichloride with abiraterone and prednisone/prednisolone should be considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
