INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325329, 'Abiraterone', 'Treprostinil', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase the plasma concentrations of treprostinil, which is primarily metabolized by the isoenzyme. However, it has not been determined if the safety and efficacy of treprostinil administered subcutaneously or intravenously are altered by this interaction.', 'DDInter', 1767317474, 'Pharmacologic response to treprostinil should be monitored more closely whenever a CYP450 2C8 inhibitor is added to or withdrawn from therapy, and the treprostinil dosage adjusted as necessary. Patients should be advised to notify their physician if they experience excessive adverse effects of treprostinil such as dizziness, lightheadedness, flushing, diarrhea, edema, and unusual bleeding or bruising.', 'Metabolism', 'Pharmacologic response to treprostinil should be monitored more closely whenever a CYP450 2C8 inhibitor is added to or withdrawn from therapy, and the treprostinil dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
