INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325349, 'Abiraterone', 'Ziprasidone', 'Major', 'Ziprasidone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 1767317474, 'Coadministration of ziprasidone with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of ziprasidone with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325350, 'Acrivastine', 'Botulinum toxin type A', 'Moderate', 'Use of anticholinergic drugs after administration of botulinum toxin may potentiate systemic anticholinergic effects such as dry mouth, blurred vision, and urinary disorders. Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive or synergistic anticholinergic effects may occur when these agents are used together.', 'DDInter', 1767317474, 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) are used after administration of botulinum toxin.', 'Synergism', 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325351, 'Amikacin', 'Botulinum toxin type A', 'Major', 'The effects of botulinum toxin may be potentiated by neuromuscular blockers, aminoglycoside antibiotics, and other drugs that can interfere with neuromuscular transmission. Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive neuromuscular blocking effects may occur when these agents are used together.', 'DDInter', 1767317474, 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission. These drugs should be avoided in patients suspected of having botulinum toxin overdose or poisoning. Because the effects of botulinum toxin products may spread from the area of injection, patients should be monitored closely for adverse effects such as asthenia, generalized muscle weakness, diplopia, blurred vision, ptosis, dysphagia, dysphonia, dysarthria, urinary incontinence and breathing difficulties, which can occur hours to weeks after injection.', 'Synergism', 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission.', NULL, NULL, 'Major', 'pharmacodynamic');
