INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (2756, 'Acalabrutinib', 'Ceritinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme. Increased acalabrutinib exposure may potentiate the risk of toxicities such as hemorrhage, infection, cytopenias, malignancies, and atrial fibrillation or flutter.', NULL, 'Concomitant use of acalabrutinib with potent CYP450 3A4 inhibitors should generally be avoided, particularly those that are intended for chronic administration.', NULL, 'Concomitant use of acalabrutinib with potent CYP450 3A4 inhibitors should generally be avoided, particularly those that are intended for chronic administration. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist and the CYP450 3A4 inhibitor is used short-term for 7 days or less, consider interrupting or delaying initiation of acalabrutinib treatment until therapy with the inhibitor is complete.', 'Metabolism', 'Acalabrutinib, Dacomitinib, Tepotinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Afatinib, Tivozanib, Axitinib, More', 'Trametinib, Nintedanib, Ceritinib, Afatinib, Dacomitinib, Tivozanib, Alectinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2756/', '[1] "Product Information. Calquence (acalabrutinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Calquence (acalabrutinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications