    ) VALUES (1325771, 'Isotretinoin', 'Adapalene (topical)', 'Moderate', 'Concomitant use of isotretinoin with topical exfoliative or keratolytic anti-acne agents may increase local skin irritation.', NULL, 'Concurrent use should generally be avoided.', NULL, 'Concurrent use should generally be avoided.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317474);
