    ) VALUES (1325775, 'Isotretinoin', 'Anisindione', 'Moderate', 'Limited data suggest that isotretinoin may reduce the hypoprothrombinemic effect of warfarin. The mechanism of interaction is unknown.', NULL, 'Until further information is available, INR should be monitored more closely whenever isotretinoin is added to or withdrawn from therapy, and the warfarin dosage adjusted as necessary.', NULL, 'Until further information is available, INR should be monitored more closely whenever isotretinoin is added to or withdrawn from therapy, and the warfarin dosage adjusted as necessary. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317474);
