INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6020, 'Tramadol', 'Ozanimod', 'Major', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the serotonergic activity of tramadol and increase the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors. The mechanism is an additive pharmacodynamic effect resulting from MAOI inhibition of serotonin metabolism. Patients receiving this combination of drugs may also experience an increased risk of seizures due to lowering of seizure threshold.', NULL, 'Concurrent use of tramadol and MAOIs or other agents that possess MAOI activity (e.', NULL, 'Concurrent use of tramadol and MAOIs or other agents that possess MAOI activity (e.g., furazolidone, procarbazine) is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with tramadol.', 'Synergism', 'Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6024/', '[1] Egberts AC, ter Borg J, Brodie-Meijer CC "Serotonin syndrome attributed to tramadol addition to paroxetine therapy." Int Clin Psychopharmacol 12 (1997): 181-2[2] Mason BJ, Blackburn KH "Possible serotonin syndrome associated with tramadol and sertraline coadministration." Ann Pharmacother 31 (1997): 175-7[3] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[4] Sternbach H "The seroton