INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6036, 'Tramadol', 'Pentazocine', 'Major', 'Concomitant use of tramadol increases the seizure risk in patients taking other opioids. These agents are often individually epileptogenic and may have additive effects on seizure threshold during coadministration. CNS- and respiratory-depressant effects may also be additive. In patients who have been previously dependent on or chronically using opioids, tramadol can also reinitiate physical dependence or precipitate withdrawal symptoms. Mixed opioid agonist-antagonist analgesics such as buprenorphine, butorphanol, nalbuphine, and pentazocine may theoretically decrease the analgesic effects of tramadol or cause withdrawal symptoms in patients who have been taking tramadol.', NULL, 'Concomitant use of tramadol and other opioids, including mixed agonist/antagonist analgesics, should be avoided in general.', NULL, 'Concomitant use of tramadol and other opioids, including mixed agonist/antagonist analgesics, should be avoided in general. Tramadol should not be used in opioid-dependent patients, and use in patients who are chronically on opioids is also not recommended. Tramadol is contraindicated in patients with acute opioid intoxication. Tramadol dosage should be reduced if it must be used in patients receiving opioids. Patients should be monitored for development of seizures and CNS and respiratory depression.', 'Synergism', 'Pentazocine, Acetylsalicylic acid, Acetaminophen, Ibuprofen', 'Tramadol', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6040/', '[1] "Product Information. Ultram (tramadol)." McNeil Pharmaceutical, Raritan, NJ.[2] Miller LG "Recent developments in t