INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6079, 'Tramadol', 'Quetiapine', 'Major', 'Concomitant use of opioids such as tramadol with other central nervous system (CNS) depressants such as quetiapine may result in hypotension, profound sedation, respiratory depression, coma, and death. There is some concern that quetiapine may have additive cardiovascular effects in combination with other drugs such as tramadol that are known to prolong the QT interval of the electrocardiogram.', NULL, 'The use of tramadol in conjunction with quetiapine should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of tramadol in conjunction with quetiapine should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. Particular care should be exercised in patients suspected to be at an increased risk of torsade de pointes. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope', 'Synergism', 'Naloxone', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6083/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] Cerner