INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6085, 'Tramadol', 'Remifentanil', 'Major', 'Concomitant use of tramadol with other central nervous system (CNS) depressants including other opioids may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension and seizures may also be increased. In patients who have been previously dependent on or chronically using opioids, tramadol can reinitiate physical dependence or precipitate withdrawal symptoms.', NULL, 'Concomitant use of tramadol with other opioids should generally be avoided.', NULL, 'Concomitant use of tramadol with other opioids should generally be avoided. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients should be monitored closely for development of seizures, respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. Cough medications containing opioids (e.g., codeine, hydrocodone) should not be prescribed to patients on tramadol. In addition, tramadol should not be used in opioid-dependent patients, and use in patients who are chronically on opioids is also not recommended. Tramadol is contraindicated in patients with acute opioid intoxication.', 'Synergism', 'Acetylsalicylic acid, Ibuprofen, Celecoxib', 'Etomidate', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6089/', '[1] "Product Information. Ultram (tramadol)." McNeil Pharmaceutical, Raritan, NJ.[2] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.[3] "Product Information. Fycompa (p