INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6188, 'Aminolevulinic acid', 'Acetazolamide', 'Major', 'Patients exposed to photosensitizing agents may be at increased risk of a phototoxic skin reaction (severe sunburn) during use of aminolevulinic acid.', NULL, 'Photosensitizing agents (e.', NULL, 'Photosensitizing agents (e.g., fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, St. John''s wort) and topical preparations containing aminolevulinic acid should generally not be administered for 24 hours before and after oral administration of aminolevulinic acid.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Rosuvastatin, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6192/', '[1] "Product Information. Gleolan (aminolevulinic acid)." NX Development Corp, Lexington, KY.[2] "Product Information. Nulibry (fosdenopterin)." Origin Biosciences Inc, Boston, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6189, 'Aminolevulinic acid (topical)', 'Acetazolamide', 'Moderate', 'Concomitant use of aminolevulinate topical preparations with other known photosensitizing agents may enhance the phototoxic reaction to photodynamic therapy. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of treated areas to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) during the period between application of aminolevulinic acid or methyl aminolevulinate and photoactivation, and for 48 hours post-illumination. Concomitant use with other topical medicinal products should be avoided.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Rosuvastatin, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6193/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Metvixia (methyl aminolevulinate topical)." Galderma Laboratories Inc, Cranbury, NJ.[4] "Product Information. Levulan Kerastick (aminolevulinic acid)" Berlex, Richmond, CA.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6190, 'Amiodarone', 'Acetazolamide', 'Major', 'Amiodarone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with amiodarone. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Ascorbic acid, Sulfadiazine, Lactic acid, Clindamycin, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, Furazolidone, Oxytetracycline, Zonisamide, More', 'Propafenone, Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6194/', '[1] Antonelli D, Atar S, Freedberg NA, Rosenfeld T "Torsade de pointes in patients on chronic amiodarone treatment: contributing factors and drug interactions." Isr Med Assoc J 7 (2005): 163-5[2] "Product Information. Amiodarone Hydrochloride (amiodarone)." Bedford Laboratories, Bedford, OH.[3] Libersa CC, Brique SA, Motte KB, et al. "Dramatic inhibition of amiodarone metabolism induced by grapefruit juice." Br J Clin Pharmacol 49 (2000): 373-8[4] Meng X, Mojaverian P, Doedee M, Lin E, Weinryb I, Chiang ST, Kowey PR "Bioavailability of Amiodarone tablets administered with and without food in healthy subjects." Am J Cardiol 87 (2001): 432-5[5] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6191, 'Amisulpride', 'Acetazolamide', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Ciclopirox, Darunavir, More', 'Mesoridazine, Quetiapine, Methotrimeprazine, Asenapine, Perphenazine, Thiothixene, Pimavanserin, Lumateperone, Olanzapine, Iloperidone, Triflupromazine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6195/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
