INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6313, 'Acetazolamide', 'Levacetylmethadol', 'Major', 'Levomethadyl acetate can cause QT interval prolongation and torsades de pointes. Coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in an elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes.', NULL, 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with levomethadyl acetate. A 12-lead ECG should be performed before initiating therapy, 12 to 14 days after initiating therapy, and periodically thereafter. Patients should be advised to immediately seek medical attention if they experience palpitations, dizziness, lightheadedness, fainting, or seizures.', 'Synergism', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Clindamycin, Trichlormethiazide, Ciclopirox, Probenecid, More', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Methadone, Nalmefene, Nicotine, Buprenorphine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6317/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[3] Cohen J "Long-term efficacy and safety of terazosin alone and in combination with other antihypertensive agents." Am Heart J 122 (1991): 919-25[4] Pool JL "Combination antihypertensive therapy with terazosin and other antihypertensive agents: results of clinical trials." Am Heart J 122 (1991): 926-31[5] Achari R, Laddu A "Terazosin: a new alpha adrenoceptor blocking drug." J Clin Pharmacol 32 (1992): 520-3[6] Kuokkanen K, Mattila MJ "Demonstration of an additive antihypertensive effect of prazosin and polythiazide in out-patient." Curr Ther Res Clin Exp 17 (1975): 431-6[7] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.', 1767369485);
