INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6360, 'Primidone', 'Acetazolamide', 'Moderate', 'Several case reports suggest that primidone absorption may be decreased or delayed when acetazolamide is coadministered. Plasma levels of primidone and its two metabolites may be decreased, as may seizure control.', NULL, 'Patients should be monitored for loss of seizure control.', NULL, 'Patients should be monitored for loss of seizure control. If an interaction is suspected or observed, it may be necessary to discontinue acetazolamide, to implement alternative antiseizure therapy, or to do both.', 'Absorption', 'Suvorexant, Methohexital, Methylphenobarbital, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Butabarbital, Secobarbital, Chloral hydrate, Paraldehyde, More', 'Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Sulfadiazine, Lactic acid, Amphotericin B, Trichlormethiazide, Ciclopirox, Ketoconazole, Probenecid, Tioconazole, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6364/', '[1] Syversen GB, Morgan JP, Weintraub M, Myers GJ "Acetazolamide-induced interference with primidone absorption." Arch Neurol 34 (1977): 80-4', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6361, 'Promazine', 'Acetazolamide', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Mesoridazine, Quetiapine, Methotrimeprazine, Asenapine, Perphenazine, Thiothixene, Pimavanserin, Lumateperone, Olanzapine, Iloperidone, Triflupromazine, More', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Trichlormethiazide, Ciclopirox, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6365/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
