INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6557, 'Famotidine', 'Cyanocobalamin', 'Minor', 'By reducing or suppressing gastric acid secretion, H2-receptor antagonists and proton pump inhibitors may interfere with the gastrointestinal absorption of vitamin B12, a process that is dependent on the presence of gastric acid and pepsin. Clinical studies have shown that dietary (i.e., protein-bound) vitamin B12 malabsorption can occur during treatment with these agents, particularly proton pump inhibitors, although the likelihood of developing clinically significant deficiency over time is unknown.', NULL, '-', NULL, '-', 'Absorption', 'Hydroxocobalamin, Iron protein succinylate, Iron, Ferrous gluconate, Ferric maltol, Iron sucrose, Ferrous fumarate', 'Tinidazole, Amoxicillin, Tetracycline, Bismuth subcitrate potassium, Rifabutin, Vonoprazan, Levofloxacin', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6561/', '[1] Lavy NW "Omeprazole and vitamin B12." Ann Intern Med 121 (1994): 74[2] Salom IL, Silvis SE, Doscherholmen A "Effect of cimetidine on the absorption of vitamin B12." Scand J Gastroenterol 17 (1982): 129-31[3] Dutta SK "Vitamin b-12 malabsorption and omeprazole therapy." J Am Coll Nutr 13 (1994): 544-5[4] Marcuard SP, Albernaz L, Khazanie PG "Omeprazole therapy causes malabsorption of cyanocobalamin (vitamin-b12)." Ann Intern Med 120 (1994): 211-5[5] Bradford GS, Taylor CT "Omeprazole and vitamin B-12 deficiency." Ann Pharmacother 33 (1999): 641-3', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6558, 'Famotidine', 'Dabrafenib', 'Moderate', 'Drugs that alter the pH of the upper gastrointestinal tract may affect the solubility of dabrafenib and reduce its bioavailability. The interaction has not been formally studied with H2-receptor antagonists or antacids; however, the solubility of dabrafenib is known to be pH-dependent. It has been reported to be very slightly soluble at pH 1 and practically insoluble above pH 4 in aqueous media.', NULL, 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids.', NULL, 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids. The potential for diminished therapeutic effects of dabrafenib should be considered, and pharmacologic response should be closely monitored.', 'Absorption', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Esomeprazole, Amoxicillin, More', 'Tepotinib, Larotrectinib, Avapritinib, Fedratinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Duvelisib, Tivozanib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6562/', '[1] "Product Information. Prevacid (lansoprazole)." TAP Pharmaceuticals Inc, Deerfield, IL.[2] "Product Information. Zantac (ranitidine)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.[4] "Product Information. Dexilant (dexlansoprazole)." Takeda Pharmaceuticals America, Lincolnshire, IL.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Aciphex (rabeprazole)" Janssen Pharmaceuticals, Titusville, NJ.[8] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6559, 'Famotidine', 'Dacomitinib', 'Moderate', 'INTERVAL: Coadministration with proton pump inhibitors (PPIs) may decrease the plasma concentrations of dacomitinib. The proposed mechanism is a pH-dependent reduction in dissolution or absorption of dacomitinib due to prolonged gastric acid suppression induced by PPIs.', NULL, 'The manufacturer recommends taking dacomitinib at least 6 hours before or 10 hours after H2-receptor antagonists.', NULL, 'The manufacturer recommends taking dacomitinib at least 6 hours before or 10 hours after H2-receptor antagonists. As an alternative, locally-acting antacids may be considered. Concomitant use of dacomitinib with PPIs should generally be avoided.', 'Absorption', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Clarithromycin, Vonoprazan, Amoxicillin', 'Tepotinib, Larotrectinib, Avapritinib, Fedratinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Duvelisib, Tivozanib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6563/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Vizimpro (dacomitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
