INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6671, 'Famotidine', 'Neratinib', 'Major', 'Coadministration with proton pump inhibitors or H2-receptor antagonists may significantly decrease the oral bioavailability of neratinib. The solubility of neratinib is pH-dependent and increases as neratinib becomes protonated at acidic pH, thus an increase in pH may interfere with its absorption.', NULL, 'Concomitant use of neratinib with proton pump inhibitors and H2-receptor antagonists should generally be avoided.', NULL, 'Concomitant use of neratinib with proton pump inhibitors and H2-receptor antagonists should generally be avoided. In addition, short-acting antacids could be considered if acid-suppression therapy is required, with neratinib dosing separated by at least three hours after antacid administration. If coadministered with a gastric acid reducing agent, increasing the dose of neratinib is unlikely to compensate for the loss of bioavailability.', 'Absorption', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Vonoprazan, Amoxicillin', 'Tepotinib, Larotrectinib, Avapritinib, Fedratinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Duvelisib, Tivozanib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6675/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin