INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6698, 'Famotidine', 'Phenytoin', 'Moderate', 'Coadministration with famotidine or ranitidine may rarely increase the plasma concentrations of phenytoin, resulting in toxicity. The mechanism of interaction is unknown.', NULL, 'Until more information is available, caution is advised if phenytoin is prescribed in combination with famotidine or ranitidine, particularly in elderly patients.', NULL, 'Until more information is available, caution is advised if phenytoin is prescribed in combination with famotidine or ranitidine, particularly in elderly patients. Clinicians should be alert for signs and symptoms of phenytoin toxicity such as ataxia, incoordination, tremor, nystagmus, hypotension, slurred speech, lethargy, nausea, vomiting, mental confusion, and psychosis. The possibility of an interaction should be considered if toxicity occurs shortly (e.g., within a month) after initiation or change of dosage of the H2-receptor antagonist. Both phenytoin and the H2-receptor antagonist may need to be withdrawn until the patient recovers.', 'Others', 'Brivaracetam, Cenobamate, Methylphenobarbital, Ethosuximide, Lacosamide, Ethotoin, Rufinamide, Paramethadione, Perampanel, Ganaxolone, Vigabatrin, More', 'Tetracycline, Dexlansoprazole, Bismuth subcitrate potassium, Nizatidine, Levofloxacin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6702/', '[1] Richards DA "Comparative pharmacodynamics and pharmacokinetics of cimetidine and ranitidine." J Clin Gastroenterol 5 (1983): 81-90[2] Bramhall D, Levine M "Possible interaction of ranitidine with phenytoin." Drug Intell Clin Pharm 22 (1988): 979-80[3] Powell JR, Donn KH "His