INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6799, 'Dabrafenib', 'Ranitidine', 'Moderate', 'Drugs that alter the pH of the upper gastrointestinal tract may affect the solubility of dabrafenib and reduce its bioavailability. The interaction has not been formally studied with H2-receptor antagonists or antacids; however, the solubility of dabrafenib is known to be pH-dependent. It has been reported to be very slightly soluble at pH 1 and practically insoluble above pH 4 in aqueous media.', NULL, 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids.', NULL, 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids. The potential for diminished therapeutic effects of dabrafenib should be considered, and pharmacologic response should be closely monitored.', 'Absorption', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Esomeprazole, Amoxicillin, More', 'Entrectinib, Pralsetinib, Asciminib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, Ribociclib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6803/', '[1] "Product Information. Prevacid (lansoprazole)." TAP Pharmaceuticals Inc, Deerfield, IL.[2] "Product Information. Zantac (ranitidine)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.[4] "Product Information. Dexilant (dexlansoprazole)." Takeda Pharmaceuticals America, Lincolnshire, IL.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Aciphex (rabeprazole)" Janssen Pharmaceuticals, Titusville, NJ.[8] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6800, 'Dacomitinib', 'Ranitidine', 'Moderate', 'INTERVAL: Coadministration with proton pump inhibitors (PPIs) may decrease the plasma concentrations of dacomitinib. The proposed mechanism is a pH-dependent reduction in dissolution or absorption of dacomitinib due to prolonged gastric acid suppression induced by PPIs.', NULL, 'The manufacturer recommends taking dacomitinib at least 6 hours before or 10 hours after H2-receptor antagonists.', NULL, 'The manufacturer recommends taking dacomitinib at least 6 hours before or 10 hours after H2-receptor antagonists. As an alternative, locally-acting antacids may be considered. Concomitant use of dacomitinib with PPIs should generally be avoided.', 'Absorption', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Clarithromycin, Vonoprazan, Amoxicillin', 'Entrectinib, Pralsetinib, Asciminib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, Ribociclib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6804/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Vizimpro (dacomitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6801, 'Dasatinib', 'Ranitidine', 'Major', 'Nonclinical data indicate that the solubility of dasatinib is pH-dependent. Therefore, long-term suppression of gastric acid secretion by H2-receptor antagonists or proton pump inhibitors is likely to reduce dasatinib systemic exposure.', NULL, 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended.', NULL, 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended. If gastric acidity management is necessary, antacids should be considered in place of these agents. However, patients treated with dasatinib should avoid taking antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) for at least two hours before and two hours after administration of dasatinib.', 'Absorption', 'Tinidazole, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 'Entrectinib, Pralsetinib, Asciminib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, Ribociclib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6805/', '[1] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6802, 'Delavirdine', 'Ranitidine', 'Moderate', 'Theoretically, H2 antagonists and proton pump inhibitors may decrease the gastrointestinal absorption of delavirdine. The proposed mechanism is a pH-dependent reduction in drug dissolution or absorption.', NULL, 'While the clinical significance of this potential interaction is unknown, the manufacturer recommends against the chronic use of these drugs with delavirdine due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of delavirdine.', NULL, 'While the clinical significance of this potential interaction is unknown, the manufacturer recommends against the chronic use of these drugs with delavirdine due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of delavirdine.', 'Absorption', 'Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Dolutegravir, Cobicistat, More', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Vonoprazan, Amoxicillin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6806/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
