INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6844, 'Ranitidine', 'Metformin', 'Moderate', 'Ranitidine is a cationic drug and theoretically could decrease the excretion of metformin by competing for renal tubular transport. Although this interaction has not been specifically reported for ranitidine, cimetidine (also a cationic drug) has been reported to interact with metformin in this manner. Increased metformin levels may increase the risk of lactic acidosis.', NULL, 'If ranitidine and metformin must be used together, particularly slow and cautious titration of metformin dosage is recommended.', NULL, 'If ranitidine and metformin must be used together, particularly slow and cautious titration of metformin dosage is recommended. The maximal dose of metformin probably also should be reduced until further information about this interaction is available. Patients should be advised to monitor their blood glucose and to promptly notify their physician if they experience possible signs of lactic acidosis such as malaise, myalgia, respiratory distress, hyperventilation, slow or irregular heartbeat, somnolence, abdominal upset, or other unusual symptoms.', 'Excretion', 'Lixisenatide, Albiglutide, Guar gum, Canagliflozin, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Troglitazone, Ertugliflozin, Semaglutide, More', 'Tinidazole, Dexlansoprazole, Bismuth subcitrate potassium, Rifabutin, Esomeprazole, Vonoprazan', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6848/', '[1] Somogyi A, Stockley C, Keal J, Rolan P, Bochner F "Reduction of metformin renal tubular secretion by cimetidine in man." Br J Clin Pharmacol 23 (1987): 545-51[2] "Product Information. Glucopha