INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6969, 'Indinavir', 'Rabeprazole', 'Moderate', 'Proton pump inhibitors may decrease the gastrointestinal absorption of indinavir. The proposed mechanism is a pH-dependent reduction in drug dissolution or absorption. The interaction has been reported specifically with omeprazole but may be expected with other proton pump inhibitors based on their common pharmacologic action.', NULL, 'Caution is advised during the chronic use of these drugs with indinavir due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of indinavir.', NULL, 'Caution is advised during the chronic use of these drugs with indinavir due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of indinavir. Patients should be monitored for altered antiviral effectiveness.', 'Absorption', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Dolutegravir, Oseltamivir, More', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Cimetidine, Nizatidine, Amoxicillin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6973/', '[1] Burger DM, Hugen PWH, Kroon FP, Groeneveld P, Brinkman K, Foudraine NA, Sprenger H, Koopmans PP, Hekster YA "Pharmacokinetic interaction between the proton pump inhibitor omeprazole and the HIV protease inhibitor indinavir." AIDS 12 (1998): 2080-2[2] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[3] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[4] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman S "Single-dose pharmacokinetics of indinavir and the effect of food." Antimicrob Agents Chemother 42 (1998): 332-8', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6970, 'Iron protein succinylate', 'Rabeprazole', 'Moderate', 'The profound hypochlorhydria induced by proton pump inhibitors (PPIs) may impair the gastrointestinal absorption of nonheme iron, a process that is dependent on an acidic environment.', NULL, 'Patients with iron deficiency may not respond adequately to oral iron replacement therapy during coadministration of proton pump inhibitors.', NULL, 'Patients with iron deficiency may not respond adequately to oral iron replacement therapy during coadministration of proton pump inhibitors. If an interaction is suspected after ruling out other causes, it may be appropriate to discontinue the proton pump inhibitor or consider administering iron parenterally.', 'Absorption', 'Tinidazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Rifabutin, Nizatidine, Ranitidine, Clarithromycin, Amoxicillin, More', 'Iron sucrose', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6974/', '[1] "Product Information. Prilosec (omeprazole)." Merck & Co, Inc, West Point, PA.[2] "Product Information. Kapidex (dexlansoprazole)." Takeda Pharmaceuticals America, Lincolnshire, IL.[3] Hutchison C, Geissler CA, Powell JJ, Bomford A "Proton pump inhibitors suppress absorption of dietary non-haem iron in hereditary haemochromatosis." Gut 56 (2007): 1291-5[4] "Product Information. Protonix (pantoprazole)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] Nand S, Tanvetyanon T "Proton pump inhibitors and iron deficiency: is the connection real?" South Med J 97 (2004): 799[6] Sharma VR, Brannon MA, Carloss EA "Effect of omeprazole on oral iron replacement in patients with iron deficiency anemia." South Med J 97 (2004): 887-9[7] "Product Information. Aciphex (rabeprazole)" Janssen Pharmaceuticals, Titusville, NJ.[8] Stewart CA, Termanini B, Sutliff VE, et al "Iron absorption in patients with Zollinger-Ellison syndrome treated with long-term gastric acid antisecretory therapy." Aliment Pharmacol Ther 12 (1998): 83-98[9] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[10] "Product Information. Prevacid (lansoprazole)." TAP Pharmaceuticals Inc, Deerfield, IL.[11] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[12] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767369485);
