INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6974, 'Rabeprazole', 'Lapatinib', 'Moderate', 'Nonclinical data indicate that the solubility of lapatinib is pH-dependent. Therefore, long-term suppression of gastric acid secretion by H2-receptor antagonists or proton pump inhibitors may reduce lapatinib systemic exposure.', NULL, 'Caution may be advisable if lapatinib is used in combination with H2-receptor antagonists or proton pump inhibitors.', NULL, 'Caution may be advisable if lapatinib is used in combination with H2-receptor antagonists or proton pump inhibitors. Patients should be monitored for potentially reduced therapeutic response to lapatinib.', 'Absorption', 'Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Amoxicillin, Ketoprofen, Piroxicam, Rofecoxib, Meclofenamic acid, Sulindac, Etodolac, More', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, Ribociclib, Cobimetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6978/', '[1] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6975, 'Rabeprazole', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor. When oral midazolam was administered concomitantly with and at 2 or 4 hours after administration of lefamulin tablets, mean midazolam peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 100% and 200%, respectively.', NULL, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vardenafil, vinca alkaloids). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever oral lefamulin is added to or withdrawn from therapy.', 'Metabolism', 'Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Ranitidine, Vonoprazan, Amoxicillin, Ketoprofen, Piroxicam, Rofecoxib, More', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Daptomycin, Telavancin, Methenamine, Spectinomycin, Fosfomycin, Tedizolid, Dalbavancin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6979/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6976, 'Rabeprazole', 'Levothyroxine', 'Moderate', 'Concurrent administration of proton pump inhibitors (PPI) may decrease the oral bioavailability of levothyroxine. Pharmacologic effects of levothyroxine may be reduced. The mechanism of interaction is suspected to be PPI induced hypochlorhydria leading to reduced levothyroxine absorption since gastric acidity is an essential requirement for levothyroxine absorption.', NULL, 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors.', NULL, 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors. Consider the alteration in gastric pH caused by the PPI. Some authorities recommend separating administration of PPI and levothyroxine by several hours, however there are no studies showing improved absorption when PPIs are administered separately from levothyroxine. If concomitant administration is necessary, consider monitoring TSH level and watching for clinical evidence of reduced levothyroxine effects. Patients should be advised to contact their physician if they experience symptoms of hypothyroidism, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Absorption', 'Tinidazole, Bismuth subcitrate potassium, Levofloxacin, Vonoprazan, Fenoprofen, Phenylbutazone, Chondroitin sulfate, Glucosamine, Tolmetin, Meclofenamic acid, Phenylbutazone, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6980/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[6] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
