INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3348, 'Dofetilide', 'Lidocaine', 'Major', 'Dofetilide should not be used with Class I or other Class III antiarrhythmic agents due to the potential for additive effects on myocardial refractoriness. Many of these agents, including dofetilide, can also cause prolongation of the QT interval, thus concomitant use may increase the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', NULL, 'Class I (e.', NULL, 'Class I (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, ibutilide, sotalol) antiarrhythmic agents should be withheld for at least 3 half-lives before administering dofetilide. In the case of amiodarone with its unpredictable pharmacokinetics, dofetilide should not be initiated until serum amiodarone levels are below 0.3 mcg/mL or amiodarone has been withdrawn for at least three months.', 'Synergism', 'Moricizine, Lidocaine, Flecainide', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Tripelennamine, Pheniramine, Bacitracin, Flurbiprofen, Chlorhexidine, Povidone-iodine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3348/', '[1] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3349, 'Dopamine', 'Lidocaine', 'Minor', 'Limited data suggest that low dose dopamine (2 mcg/kg/min) increases lidocaine dose requirements to maintain epidural anesthesia by as much as 45% without a significant change in plasma lidocaine concentrations. The mechanism may be related to enhanced hepatic lidocaine elimination. Clinical monitoring of anesthetic response is recommended and lidocaine dosage adjustments may be necessary.', NULL, '-', NULL, '-', 'Metabolism', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Diphenhydramine, Tripelennamine, Pheniramine, Bacitracin, Flurbiprofen, Ibuprofen, More', 'Droxidopa, Norepinephrine, Midodrine, Amrinone, Angiotensin II, Ephedrine, Milrinone, Phenylephrine, Fenoldopam, Metaraminol, Isoprenaline, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3349/', '[1] Sprung J, Bourke DL, Schoenwald PK, Whalley D, Gottlieb A, Mascha EJ, Kapural L "Small-dose dopamine increases epidural lidocaine requirements during peripheral vascular surgery in elderly patients." Anesth Analg 90 (2000): 388-92', 1767369485);
