INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3373, 'Fostamatinib', 'Lidocaine', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Diphenhydramine, Tripelennamine, Doxepin, Pheniramine, Promethazine, Bacitracin, More', 'Eltrombopag, Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, Coagulation Factor IX Human', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3373/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3374, 'Gentamicin', 'Lidocaine', 'Minor', 'Limited in vitro data suggest that some aminoglycosides may enhance the neuromuscular blocking properties of lidocaine. Data are available for neomycin only. No special precautions are necessary.', NULL, '-', NULL, '-', 'Synergism', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Diphenhydramine, Tripelennamine, Pheniramine, Povidone-iodine, Quinidine, Propafenone, More', 'Ganciclovir, Norfloxacin, Rifamycin, Oxytetracycline, Polymyxin B, Lomefloxacin, Trifluridine, Benzylpenicillin, Ampicillin, Levofloxacin, Povidone-iodine, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3374/', '[1] Bruckner J, Thomas KC, Bikhazi GB, Foldes FF "Neuromuscular drug interactions of clinical importance." Anesth Analg 59 (1980): 678-82', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3375, 'Ginkgo biloba', 'Lidocaine', 'Moderate', 'Certain preparations of ginkgo biloba have been reported to induce seizures. There may be a theoretical risk of increased seizure potential when used with other agents that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. Ginkgo products may contain varying amounts of 4''-O-methylpyridoxine (ginkgotoxin), a known neurotoxin found primarily in ginkgo biloba seeds but also detected in lesser amounts in the leaves. In vivo, 4''-O-methylpyridoxine competes with vitamin B6, which causes an indirect inhibition of glutamate decarboxylase and subsequent decrease in the formation of gamma-aminobutyric acid (GABA) in the brain. There have been published case reports of generalized convulsions and vomiting within several hours after ingestion of large amounts of ginkgo nuts/seeds, including in young children and healthy individuals with no known personal or family history of epilepsy.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Because of inconsistencies in formulation and potency of commercial herbal preparations, there is no way to verify without laboratory testing if and in what quantity 4''-O-methylpyridoxine may be present in a given ginkgo preparation. Patients treated with agents that can lower the seizure threshold should preferably avoid the use of products containing ginkgo biloba.', 'Synergism', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Diphenhydramine, Tripelennamine, Pheniramine, Neomycin, Chlorhexidine, Povidone-iodine, More', 'Galantamine, Tacrine, Aducanumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3375/', '[1] Kajiyama Y, Fujii K, Takeuchi H, Manabe Y "Ginkgo seed poisoning." Pediatrics 109 (2002): 325-7[2] Granger AS "Ginkgo biloba precipitating epileptic seizures." Age Ageing 30 (2001): 523-5[3] Miller LG "Herbal medicinals: selected clinical considerations focusing on known or potential drug-herb interactions." Arch Intern Med 158 (1998): 2200-11[4] Gregory PJ "Seizure associated with Ginkgo biloba?." Ann Intern Med 134 (2001): 344[5] Harms SL, Garrard J, Schwinghammer P, Eberly LE, Chang Y, Leppik IE "Ginkgo biloba use in nursing home elderly with epilepsy or seizure disorder." Epilepsia 47 (2006): 323-9[6] Spinella M "Herbal medicines and epilepsy: the potential for benefit and adverse effects." Epilepsy Behav 2 (2001): 524-32[7] Miwa H, Iijima M, Tanaka S, Mizuno Y "Generalized convulsions after consuming a large amount of Gingko nuts." Epilepsia 42 (2001): 280-1[8] Kupiec T, Raj V "Fatal seizures due to potential herb-drug interactions with Ginkgo biloba." J Anal Toxicol 29 (2005): 755-8', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3376, 'Givosiran', 'Lidocaine', 'Moderate', 'Coadministration with givosiran may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2 and/or CYP450 2D6. The proposed mechanism is decreased clearance due to givosiran-mediated inhibition of CYP450 1A2 and 2D6 isoenzymes.', NULL, 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6.', NULL, 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6. Concomitant use should generally be avoided with CYP450 1A2 or 2D6 substrates that have a narrow therapeutic range where minimal concentration changes may lead to serious or life-threatening toxicities. If concomitant use is unavoidable, the dosage of the CYP450 1A2 or 2D6 substrate should be reduced according to approved product labeling or clinical response and tolerance. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever givosiran is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Tripelennamine, Pheniramine, Povidone-iodine, Flurbiprofen, Chlorhexidine, Ibuprofen, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3376/', '[1] "Product Information. Givlaari (givosiran)." Alnylam Pharmaceuticals, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3377, 'Golimumab', 'Lidocaine', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Diphenhydramine, Tripelennamine, Doxepin, Pheniramine, Promethazine, Bacitracin, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, Upadacitinib, Siponimod, Apremilast, Inebilizumab, Cladribine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3377/', '[1] "Product Information. Skyrizi (risankizumab)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Gamifant (emapalumab)." Sobi Inc, Ardmore, PA.[5] "Product Information. Kevzara (sarilumab)." sanofi-aventis, Bridgewater, NJ.[6] "Product Information. Simponi (golimumab)." Centocor Inc, Malvern, PA.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc., Horsham, PA, PA.[9] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.[10] "Product Information. Ilumya (tildrakizumab)." Merck & Company Inc, Whitehouse Station, NJ.[11] "Product Information. Actemra (tocilizumab)." Genentech, South San Francisco, CA.[12] "Product Information. Amevive (alefacept)." Biogen, Cambridge, MA.[13] "Product Information. Remicade (infliximab)." Centocor Inc, Malvern, PA.[14] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company, Indianapolis, IN.[15] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[16] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[17] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine